package chap06.section06;

public class FieldInitValueExample {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		FieldInitValue fiv = new FieldInitValue();
		
		System.out.println(fiv.byteField); // 0
		System.out.println(fiv.shortField); // 0
		System.out.println(fiv.intField); // 0
		System.out.println(fiv.longField); // 0
		System.out.println(fiv.booleanField); // false
		System.out.println(fiv.charField); //
		System.out.println(fiv.floatField); // 0.0
		System.out.println(fiv.doubleField); // 0.0
		System.out.println(fiv.arrField); // null
		System.out.println(fiv.referenceField); //null
	}

}
